def find_groups(input_sting)
  $overall_count = 0
  $current_count = 0
  $current_index = 0
  $open_groups = 0
  $open_garbage = false
  while $current_index <= input_sting.length - 1
    analyse_letter(input_sting)
  end
  puts "#{$overall_count}"
end

def analyse_letter(full_string)
  current_letter = full_string[$current_index]
  if current_letter.eql? "!"
    $current_index += 1
  else
    if current_letter.eql? "<"
      $open_garbage = true
    elsif current_letter.eql? ">"
      $open_garbage = false
    end
    if !$open_garbage
      if current_letter.eql? "{"
        $current_count += 1
        $open_groups += 1
      elsif current_letter.eql? "}"
        if $open_groups >= 1
          $overall_count += $current_count
          $current_count -= 1
          $open_groups -= 1
        end
      end
    end
  end
  $current_index += 1
end

def find_groups2(input_sting)
  $current_index = 0
  $open_garbage = false
  $garbage_count = 0
  while $current_index <= input_sting.length - 1
    analyse_letter2(input_sting)
  end
  puts "#{$garbage_count}"
end

def analyse_letter2(full_string)
  current_letter = full_string[$current_index]
  if current_letter.eql? "!"
    $current_index += 1
  else
    if current_letter.eql? "<"
      if !$open_garbage 
        $garbage_count -= 1
      end
      $open_garbage = true
    elsif current_letter.eql? ">"
      $open_garbage = false
    end
    if $open_garbage
      $garbage_count += 1
    end
  end
  $current_index += 1
end


